		//设置转盘初始化数据/重置转盘
		function HandleSetZhuanPanInit()
		{
			$.get( '?r=/index/initDail',
				   function(data) {
						$('#flash').flash(function () {
							this.API_setZhuanPanInitData(data);
						});
					} 
			); 
		}
		
		//转盘升级请求
		//val 当前等级  1:青铜 2:白银 3:黄金 4:钻石
		function CMD_lvUp(val)
		{
			$.get( '?r=/index/levelUp',
					   function(data) {
						var dataJson = JSON.parse(data);
						HandleSetZhuanPanInit();
						if ( typeof ( dataJson.isLogin ) != 'undefined' && false == dataJson.isLogin ) {
								showlogBox(); 
								return;
							}  
							HandleSetZhuanPanInit(); 
							showMsgBox(dataJson.fail_info);
			} );
		}
		
		//转盘抽奖请求
		function CMD_getZhuanPanReward()
		{
				$.ajax({
					url  : '?r=/lottery/exection',
					type : 'POST',
					dataType : 'text',
					beforeSend : function() {
							$('#loading').show();
					   },
					error : function(){
						$('#loading').hide();
						showMsgBox('Abnormal network cause! Failed to submit. Please submit again later.');
					}, 
					success :function(data) {
					$('#loading').hide();
				    HandleSetZhuanPanInit();
					var dataJson = JSON.parse(data);
					if ( typeof ( dataJson.isLogin ) != 'undefined' && false == dataJson.isLogin ) {
							showlogBox();
							return;
						} else if ( typeof ( dataJson.success ) != 'undefined' && 1 == dataJson.success ) {
							$('#flash').flash(function () {
								this.API_setZhuanPanRewardData(data);
							});
							$('#totalCredit').text(dataJson.totalCredit);
							$('#remainCredit').text(dataJson.remainCredit);
							$('#uesdCredit').text(dataJson.uesdCredit);
							return ;
						}
						showMsgBox(dataJson.fail_info);
					}} );
		}
		
