		//点击可以领奖的星星进行领奖
		function CMD_getRewardStar(val)
		{
			$.ajax({
				url  : '?r=/get/exc',
				type : 'POST',
				data : {'starLevel':val},
				dataType : 'json',
				beforeSend : function() {
						$('#loading').show();
				   },
				error : function(){
					$('#loading').hide();
					showMsgBox('Abnormal network cause! Failed to submit. Please submit again later.');
				}, 
				success : function(data) {
					$('#loading').hide();
					if ( typeof ( data.isLogin ) != 'undefined' && false == data.isLogin ) {
						HandleSetZhuanPanInit();
						showlogBox(); 
						return;
					} else if ( typeof ( data.getCode ) != 'undefined' && data.getCode ) {
						HandleSetStarData();//登陆成功，重载页面
					}  
					showMsgBox(data.msg);
				} 
			});
		}	
		
		//转盘抽奖请求
		function HandleSetStarData()
		{
			 $.get( '?r=/index/initStar',
					   function(data) {
							$('#altContentStar').flash(function () {
								this.API_setStarData(data);
							}); 
						} 
			);  
		}
		
		/**
		*获取flash对象
		*movieName 对象id
		*/
		function getFlashMovieObject(movieName) 
		{ 
			 if (window.document[movieName])  
			 { 
					 return window.document[movieName]; 
			 } 
			 if (navigator.appName.indexOf("Microsoft Internet")==-1) 
			 { 
				   if (document.embeds && document.embeds[movieName]) 
						  return document.embeds[movieName];  
			 } else 
			 { // if (navigator.appName.indexOf("Microsoft Internet")!=-1) 
				  return document.getElementById(movieName); 
			 } 
		}		
